import * as actionTypes from '../action/type'

export const tabsReducer = (state = actionTypes.All_TODO , action) => {
    switch(actionTypes.type) {
        case actionTypes.TOGGLE_TAB :
            return action.selected ;
        default :
            return state ;
    }
} ;
